import{getData}from"./projects.js";const skillsURL="/data/skills.json",skillsContainer=document.querySelector(".skills");getData(skillsURL).then((SkillsData=>{SkillsData&&SkillsData.forEach((skill=>{if(!0===skill.isVisible){const skillElement=document.createElement("div");skillElement.classList.add("skills__skill"),skillElement.textContent=skill.name,skillsContainer.appendChild(skillElement)}}))})).catch((error=>{console.error("Error loading skills JSON",error)}));